package ru.r1b.solution.task424;

import org.junit.jupiter.api.Test;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

import java.util.stream.Stream;

import static org.junit.jupiter.api.Assertions.assertEquals;

class SolutionTest {

    public static Stream<Arguments> source() {
        return Stream.of(

                Arguments.of("ABCDDD", 3, 6),
                Arguments.of("ABAB", 2, 4),
                Arguments.of("ABAA", 0, 2),
                Arguments.of("AABABBA", 1, 4),
                Arguments.of("ABBB", 2, 4),
                Arguments.of("BAAAB", 2, 5),
                Arguments.of("QLHOSLDHOOBHFLPBSLHMSHMSRDOIFGGRTTSMKKRIENQNEECPLTJKCDMLRNNEPQAJDQFPEOGLKRBHSOMHONNTKLFHKNCHQLDBACMO", 7, 10),
                Arguments.of("", 2666, 2809)
        );
    }

    @ParameterizedTest
    @MethodSource("source")
    void characterReplacement(String s, int k, int expected) {
        assertEquals(expected, new Solution().characterReplacement(s, k));
    }

    @Test
    void maxWindow() {
        assertEquals(4, new Solution().maxWindow('A', "AABABBA".toCharArray(), 1));
    }
}